    private void btnAceptarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnAceptarActionPerformed
        // TODO add your handling code here
        //nombrePerro, yo lo declare como String
        nombrePerro = tfNombrePerro.getText();
        if(!nombrePerro.equals("")){
            lblNombrePerro.setText(nombrePerro);
            btnAceptar.setVisible(false);
            tfNombrePerro.setVisible(false);

        }else{
            JOptionPane.showMessageDialog(null, "Error cadena vacía");

        }
    }//GEN-LAST:event_btnAceptarActionPerformed

