    private void btnComerActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnComerActionPerformed
        // TODO add your handling code here:
            ImageIcon ImageIcon = new ImageIcon("comer.gif");
            imgPerro.setIcon(ImageIcon);
            crece++;
            if(crece < 3){
                lblNombrePerro.setText("Tu mascota esta creciendo.");
            }else{
                lblNombrePerro.setText("Tu mascota ha crecido, conocela en HOME");
            }           
    }//GEN-LAST:event_btnComerActionPerformed

    private void btnHomeActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnHomeActionPerformed
        // TODO add your handling code here:
        if(crece < 3){
            ImageIcon ImageIcon = new ImageIcon("GatoBebe.gif");
            imgPerro.setIcon(ImageIcon);
        }else{
            ImageIcon ImageIcon = new ImageIcon("grande.gif");
            imgPerro.setIcon(ImageIcon);
        }
         lblNombrePerro.setText(nombrePerro);
    }//GEN-LAST:event_btnHomeActionPerformed

